css:
.invoice-box {
    font-family: "Helvetica", "Arial", sans-serif;
    font-size: 12px;
    color: #333;
    padding: 20px;
    line-height: 1.4;
  }

  .invoice-header h1 {
    font-size: 20px;
    margin-bottom: 5px;
  }

  .invoice-details p {
    margin: 2px 0;
  }

  table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 15px;
  }

  th, td {
    border: 1px solid #ccc;
    padding: 8px;
    text-align: left;
  }

  th {
    background-color: #f9f9f9;
    font-weight: bold;
  }

  .totals {
    text-align: right;
    margin-top: 15px;
    font-size: 14px;
    font-weight: bold;
  }
